import React, { Component } from 'react';
import Data from '../data';

class Pokemon extends Component {
  RenderImagePokemon(pokemon) {
    return (
      <div className="pokemon-content-img">
        <a className="pokemon-link" href={pokemon.moreInfo} Target="_blank">
          <img src={pokemon.image} alt="imagem do pokemon" />
        </a>
      </div>
    )
  };

  RenderInfoPokemon(pokemon) {
    return (
      <div className="pokemon-content-info">
        <span className="pokemon-info">{pokemon.name}</span>
        <span className="pokemon-info">{pokemon.tipo}</span>
        <span className="pokemon-info">
          {pokemon.averageWeight.value} 
          {pokemon.averageWeight.measurementUnit}
        </span>
      </div>)
  };

  RenderPokemon() {
    const results = Data.map(pokemon => {
      return (
        <div className="pokemon-container" key={pokemon.id}>
          {this.RenderInfoPokemon(pokemon)}
          {RenderImagePokemon(pokemon)}
        </div>
      )
    });
    return results;
  };

  render() {
    return <this.RenderPokemon />;
  }
}

export default Pokemon;
