import React, { Component } from 'react';

class Pokemon extends Component {

  RenderImagePokemon() {
    return (
      <div className="pokemon-content-img">
        <a className="pokemon-link" href={this.moreInfo} Target='_blank' rel="info-pokemon">
          <img src={this.image} alt="imagem do pokemon" />
        </a>
      </div>
    )
  };

  RenderInfoPokemon() {
    return (
      <div className="pokemon-content-info">
        <span className="pokemon-info">{this.name}</span>
        <span className="pokemon-info">{this.tipo}</span>
        <span className="pokemon-info">
          {this.props.averageWeight.value}
          {this.props.averageWeight.measurementUnit}
        </span>
      </div>)
  };

  RenderPokemon() {
      return (
        <div className="pokemon-container" key={this.id}>
          <this.RenderInfoPokemon />
          <this.RenderImagePokemon />
        </div>
      )
  };

  render() {
    return <this.RenderPokemon />
  }
}

export default Pokemon;
