import React from 'react';
import Pokemon from './Pokemon';
import Button from './Button';

class Pokedex extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            current: 0,
            pokemon: props.pokemons[0],
            length: props.pokemons.length
        }
        this.handleClick = this.handleClick.bind(this);
    }

    handleClick() {
        if(this.state.current + 1 < this.state.length) {
            this.setState((state) => { return { current: state.current + 1 } });
        } else {
            this.setState(() => { return { current: 0 } });
        }
        this.setState((state) => { return { pokemon: this.props.pokemons[state.current] } });
        console.log(this.state)
    }

    render() {
        return (
            <div className="pokedex">
                <Pokemon pokemon={ this.state.pokemon } />
                <Button handleClick={ this.handleClick }/>
                {/* <button onClick={ this.handleClick }>Aqui</button> */}
            </div>
        );
    }
}

export default Pokedex;
