import React from 'react';
import Pokemon from './Pokemon';
// import Button from './Button';

class Pokedex extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            current: 0,
            pokemon: props.pokemons[this.state.current],
            length: props.pokemons.length
        }
        this.handleClick = this.handleClick.bind(this);
    }

    handleClick() {
        if(this.state.current + 1 < this.state.length) {
            this.setState({
                current: this.state.current + 1,
            });
        } else {
            this.setState({
                current: 0,
            });
        }
        // this.setState({ pokemon: this.props.pokemons[this.state.current] })
        // console.log(this.state.pokemon)
    }

    render() {
        // console.log(this.state.pokemon)
        return (
            <div className="pokedex">
                <Pokemon pokemon={ this.state.pokemon } />
                {/* <Button eventClick={ this.handleClick } /> */}
                <button onClick={ this.handleClick }>Aqui</button>
            </div>
        );
    }
}

export default Pokedex;
