import React, { Component } from 'react';

class SelectState extends Component{
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      items: [],
    }
  }

  getFetchStates = () => {
    const URL =  'https://servicodados.ibge.gov.br/api/v1/localidades/estados/';
    fetch(URL)
      .then(res => res.json())
      .then((result) => this.setState({ isLoaded: true, items: result.sigla }))
      .catch((result) => this.setState({ ['error']: result }))
  }


  render() {
    // this.setStatesInState();
    return <div></div>
  }
}

export default SelectState;
