import React, { Component } from 'react';
import PropTypes from 'prop-types';

class Input extends Component {
  constructor(props){
    super(props);
    this.state = {
      maxCaracter: props.maxCaracter,
    }
  }
  render() {
    const { maxCaracter } = this.state;
    return <input type="text" size={100} maxLength={ maxCaracter } required/>;
  }
}

Input.propTypes = {
  maxCaracter: PropTypes.number
}

Input.defaultProps = {
  maxCaracter: 40,
}

export default Input;
