import React, { Component } from 'react';

class SelectState extends Component{
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      items: [],
    }
    this.getFetchStates(this.sortState);
  }

  getFetchStates = async (callback) => {
    const URL =  'https://servicodados.ibge.gov.br/api/v1/localidades/estados/';
    try {
      const data = await fetch(URL);
      const items = await data.json();
      this.setState({ 
        isLoaded: true, 
        items: items.map(iten => {
          return {
            id: iten.id,
            sigla: iten.sigla
          } 
        }),
      });
    } catch(e) {
      this.setState({ error: e});
    }
    callback();
  }

  sortState = () => {
    const entries = Object.values(this.state.items);
    const sorted = entries.sort((a, b) => a - b);
    const results = sorted.map((item, index) => { return { id: index, sigla: item } });
    this.setState({ items: results });
    console.log(sorted);
  }

  render() {
    const { error, isLoaded, items } = this.state;
    if(error) {
      return <div>Error: { error.message }</div>
    } else if(!isLoaded) {
      return <div>Loading...</div>
    } else {
      return (
        <select>
          {items.map(item => (
            <option key={item.id} value={ item.sigla }>{ item.sigla }</option>
          ))}
        </select>
      )
    }
  }
}

export default SelectState;
