import React, { Component } from 'react';
import Label from './Label';

class RadioButton extends Component{
  constructor(props){
    super(props);
    this.state = {
      casa: 'checked',
      apartamento: false,
    }
  } 
  handleClick = (event) => {

  }

  render() {
    const { handleChangeRadio } = this.props;
    return (
      <div className="field-residence">
        <div className="field-house">
          <Label nameField={ 'Casa' } />
          <input type="radio" id="casa" value="casa" name="residence" checked={this.state.checked} onChange={ handleChangeRadio }/>
        </div>
        <div className="field-apartament">
          <Label nameField={ 'Apartamento' } />
          <input type="radio" id="apartamento" value="apartamento" name="residence"  checked={this.state.checked} onChange={ handleChangeRadio }/>
        </div>
      </div>  
    )
  }
}

export default RadioButton;
