import React, { Component } from 'react';
import Input from './Input';
import Label from './Label';

import './Formulario.css'

class Formulario extends Component{
  constructor() {
    super();
    this.state = {
      name: '',
      email: '',
      cpf: '',
      address: '',
    }
  }

  handleChange = (event) => {
    const { target } = event;
    this.setState({
      [target.name]: target.value,
    });
  };

  isValiditAddress = (event) => {
    const isValid = event.target.value.match( /[^a-zA-Z\wÀ-ú][^']/g);
    if (isValid || event.target.value.length === 0) {
      console.log(isValid)
      console.log('Invalid');
    } else {
      console.log('VALIDO')
      console.log(event.target.value)
    }
  };

  handleChangeValidAddress = (event) => {
    this.handleChange(event);
    this.isValiditAddress(event);
  };

  render() {
    const { name, email, cpf, address } = this.state
    return (
      <form action="#" method="GET">
        <fieldset>
          <legend>Dados pessoais</legend>

          <div className="field-name">
            <Label nameField={ 'Nome:' } />
            <Input
              handleChange={ this.handleChange }
              maxLength={ '40' }
              name={ 'name' }
              value={ name }
            />
          </div>

          <div className="field-email">
            <Label nameField={ 'Email:' } />
            <Input
              handleChange={ this.handleChange }
              maxLength={ '50' }
              name={ 'email' }
              type={ 'email' }
              value={ email }
              />
          </div>

          <div className="field-cpf">
          <Label nameField={ 'CPF:' } />
            <Input
              handleChange={ this.handleChange }
              maxLength={ '11' }
              name={ 'cpf' }
              value={ cpf }
              />
          </div>

          <div className="field-endereco">
            <Label nameField={ 'Endereço:' }/>
            <Input 
              handleChange={ this.handleChangeValidAddress }
              maxLength={ '200' }
              name={ 'address' }
              value={ address }
            />
          </div>



        </fieldset>
      </form>
    );
  };
}

export default Formulario;
