import React, { Component } from 'react';
import PropTypes from 'prop-types';

class Input extends Component {
  constructor(props){
    super(props);
    this.state = {
      maxCaracter: props.maxCaracter,
      maxSizeInput: props.sizeInput
    }
  }
  render() {
    const { maxCaracter, maxSizeInput } = this.state;
    return <input type="text" size={maxSizeInput} maxLength={ maxCaracter } required/>;
  }
}

Input.propTypes = {
  maxCaracter: PropTypes.number,
  maxSizeInput: PropTypes.number
}

Input.defaultProps = {
  maxCaracter: 20,
  maxSizeInput: 40
}

export default Input;
