import React, { Component } from 'react';
import PropTypes from 'prop-types';

class InputName extends Component {

  render() {
    const { maxNameLength, maxSizeInput, nameInput, handleInputName, name } = this.props;
    return (
      <input
        maxLength={ maxNameLength }
        name={ nameInput }
        onChange={ handleInputName }
        required
        size={ maxSizeInput }
        type="text"
        value={ name }
      />
    );
  }
}

InputName.propTypes = {
  maxNameLength: PropTypes.string,
  maxSizeInput: PropTypes.string
}

InputName.defaultProps = {
  maxNameLength: "20",
  maxSizeInput: "40"
}

export default InputName;
