import React from 'react';

class Exemplo1 extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      email: '',
      saveEmail: '',
    };
    this.handlechange = this.handlechange.bind(this);
    this.onClickEnviar = this.onClickEnviar.bind(this);
  }

  handlechange({ target }) {
    this.setState({ [target.name]: target.value });
  }

  onClickEnviar() {
    this.setState((state) => ({ saveEmail: state.email }));
  }

  render() {
    const { email, saveEmail } = this.state;
    return (
      <div>
        <label htmlFor="current">
          Email:
          <input
            name="email"
            value={ email }
            onChange={ this.handlechange }
            type="email"
          />
        </label>
        <button onClick={ this.onClickEnviar } type="button">Enviar</button>
        <button type="button">Voltar</button>
        <div>
          <span>{`Valor: ${saveEmail}`}</span>
        </div>
      </div>
    );
  }
}

export default Exemplo1;
