import React from 'react';

class Sidebar extends React.Component {
  constructor(props) {
    super();

    this.state = {
      categories: [
        {
          id: 1,
          name: 'Drama',
          movies: [
            { id: 1, title: 'The boy in the striped pajamas', released: 2008 },
            { id: 2, title: 'The pursuit of hapiness', released: 2006 },
            { id: 3, title: 'Fences', released: 2016 },
          ],
        },
        {
          id: 2,
          name: 'Action',
          movies: [
            { id: 4, title: 'Shooter', released: 2007 },
          ],
        },
        {
          id: 3,
          name: 'Roamantic',
          movies: [
            { id: 4, title: 'Me before you', released: 2016 },
            { id: 6, title: 'THe lake house', released: 2006 },
          ],
        },
      ],
    };
  }

  render() {
    const { categories } = this.state;
    return (
      <aside>
        {
          categories.map((category) => (
            <div key={ category.id }>
              <h3>{category.name}</h3>
              <ul>
                {
                  category.movies.map((movie) => (
                    <li key={ movie.id }>
                      {movie.title}
                      {' '}
                      was released in
                      {' '}
                      {movie.released}
                    </li>
                  ))
                }
              </ul>
            </div>
          ))
        }
      </aside>
    );
  }
}

export default Sidebar;
